        scramble (plain) {
            var coded = "";
            try {
                var span = plain.length;
                for (var cursor = 0; cursor < span; cursor++) {
                    
                    var temp =  plain.charAt(cursor);
					coded += this.charConverter(temp);
                    coded += this.sep;
                }

            } catch (e) {
                console.log(e.Message);
                console.log(e.StackTrace);
            }

            return coded;
        }
        decipher (coded) {
            var plain= "";
            var span = coded.length;
            var buf = "";
            for (var cursor = 0; cursor < span; cursor++) {
                var temp = coded.charAt(cursor);
                if (temp != (this.sep)){
					buf += coded.charAt(cursor);
				}else {
                    plain += this.retriever(buf);
                    buf = "";
                }
            }

            return plain;
        }
		
